package day5.uniray_relational_operator;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 100;
		System.out.println(i);
		
		i+=1;
		System.out.println(i);
		
		i-=2;
		System.out.println(i);
		
		i*=3;
		System.out.println(i);
		
		i/=4;
		System.out.println(i);
		
		i%=5;
		System.out.println(i);
	}

}
